import React from 'react'
import { useEffect } from 'react';
import { useState } from 'react';
import { Fragment } from 'react'
import { ApiGetCall, ApiPostCall, ApiPutCall } from '../src/Connector';
import { Modal } from 'react-bootstrap';
import './common.css'
function Testing() {

    const [GridData, setGridData] = useState([]);
    const [addpartsPopup, setAddpartsPopup] = useState(false);
    const [FlagForAddUpdate, setFlagForAddUpdate] = useState(false);
    const [FormData, setFormData] = useState({
        name: '',
        email: '',
        contact: '',
        city: '',
        age: '',
        id:''
    });
    useEffect(() => {
        GetPartData()
        console.log(FormData)
    }, []);
    function GetPartData() {
        ApiGetCall("/api/displayDeetails").then((result) => {
            if (result == undefined || result == "") {
                alert("Something went wrong");
            } else {
                const responseRs = JSON.parse(result);
                console.log(responseRs)

                    setGridData(responseRs);

            }
        });
    }

    function SaveUser() {
        var raw = JSON.stringify({
            name:FormData.name,
            email:FormData.email,
            contact:FormData.contact,
            city:FormData.city,
            age:FormData.age
        });
        ApiPostCall("/api/addDetails", raw).then((result) => {
            if (result == undefined || result == "") {
                alert("Something went wrong");
            } else {
                const responseRs = result
                console.log(responseRs)
                setAddpartsPopup(false)
                if (responseRs.status == "success") {
                    setTimeout(function(){
                        window.location="/"
                        GetPartData()
                    },1500)
                } 
            }
        });
    };

    function GetDataById(id) {
        ApiGetCall("/api/selectOneUserDetails/"+id).then((result) => {
            if (result == undefined || result == "") {
                alert("Something went wrong");
            } else {
                const responseRs = JSON.parse(result);
                console.log(responseRs)
                setFormData({...FormData,name:responseRs.name,city:responseRs.city,contact:responseRs.contact,email:responseRs.email,age:responseRs.age,id:responseRs.user_id})
            }
        });
    };
    function EditUser() {
        const data = {
          name: FormData.name,
          email: FormData.email,
          contact: FormData.contact,
          city: FormData.city,
          age: FormData.age
        };

        fetch("/api/updateDetails/" + FormData.id, {
          method: "PUT",
          headers: {
            "Content-Type": "application/json"
          },
          body: JSON.stringify(data) // Convert data to a JSON string
        })
          .then(response => response.json())
          .then(result => {
            if (result === undefined || result === "") {
              alert("Something went wrong");
            } else {
              const responseRs = result;
              console.log(responseRs);
              setAddpartsPopup(false);
              if (responseRs.status === "success") {
                setTimeout(function() {
                  window.location = "/";
                  GetPartData();
                }, 1500);
              }
            }
          });
      }


    return (
        <Fragment>
            <div className="d-flex justify-content-between pe-0 align-items-center">
                <div >
                    <h1 className="PageHeading">Manage Customers</h1>
                </div>
                <div className=" text-center d-flex justify-content-end align-items-end"
                    onClick={(e) => { setAddpartsPopup(true); setFlagForAddUpdate(1) }}>
                    <label className="BorderBtn text-center">
                        Add Customers
                        <img src="/images/AddInventory.svg" className="img-fluid ps-2" />
                    </label>
                </div>
            </div>


            <div className="GridBox mt-2 p-3">
                <div className="col-12 ">
                    <div className='col-12 greyBox mt-3 data-table-container'>
                        <table className="table data-table mb-0">
                            <thead className='GridHeader'>
                                <tr>
                                    <th scope="col" className='cursor-pointer fw-600' >Name</th>
                                    <th scope="col" className='cursor-pointer fw-600' >Email</th>
                                    <th scope="col" className='cursor-pointer fw-600'>Contact</th>
                                    <th scope="col" className='cursor-pointer fw-600'>City</th>
                                    <th scope="col" className='cursor-pointer fw-600'>Age</th>
                                    <th scope="col" className='cursor-pointer fw-600'>Action</th>
                                </tr>
                            </thead>
                            <tbody>

                                        {GridData.map((item, i) => {
                                            var returData =
                                                <tr key={i} className={`OddEvenClass tableBorderHide `} style={{ height: '30px' }}>
                                                    <td className='py-12'>{item.name}</td>
                                                    <td className='py-12'>{item.email}</td>
                                                    <td className='py-12'>{item.contact}</td>
                                                    <td className='py-12'>{item.city}</td>
                                                    <td className='py-12'>{item.age}</td>
                                                    <td className='py-12'>
                                                        <img src="/images/EditIconNew.svg" title="Edit" className="img-fluid pe-2 cursor-pointer partsgetID"
                                                            onClick={(e) => { setAddpartsPopup(true); setFlagForAddUpdate(2); GetDataById(item.user_id)}}
                                                        />
                                                        <img src="/images/DeleteIcon.svg" title="Delete" className="img-fluid cursor-pointer" />
                                                    </td>
                                                </tr>
                                            return returData;
                                        })}


                            </tbody>
                        </table>
                    </div>
                </div>
            </div>



            <Modal show={addpartsPopup} size="l">
                <div className="pt-3 px-2">
                    <div className="d-flex justify-content-between pe-0">
                        <div>
                            {FlagForAddUpdate == 1 ?
                                <h5 className="fw-600">Add User</h5>
